import styled from 'styled-components';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Slider from 'react-slick';

const ImgSlider = (props) => {
    const Settings={
        dots:true,
        infinite:true,
        speed:500,
        slideToShow:1,
        slideToScroll:1,
        autoplay:true,

    }
    return(
           <Carousel {...Settings}>
               <div>
                   <h3>1</h3>
               </div>
               <div>
                   <h3>2</h3>
               </div>
           </Carousel>

    )
}; 

const Carousel = styled(Slider)`
      margin-top:20px;

      &>button{
          opacity:0;
          height:100%;
          width:5vw;
          z-index:1;

          &:hover{
              opacity:1;
              transition:opacity 0.2s ease 0s;
          }
      }

      ul li button{
          &:before{
              font-size:10px;
              color:rgb(150,158,171);
          }
      }

      li.slick-active button:before{
          color:white;
      }

      .slick-list {
          overflow:initial;
      }

      .slick-prev{
          left:-75px;
      }

      .slick-next{
          right:-75px;
      }
`;

export default ImgSlider;
